sys_script_client.script:
function onLoad() {

   var ga = new GlideAjax('EmailRetriever');
	ga.addParam('sysparm_name', 'getEmail');
	ga.addParam('sysparm_userid',g_user.userID);
	ga.getXML(callbackFunction);

	function callbackFunction(response) {

		try {
			jslog("Calling server side script" );
			var answerFromXML  = response.responseXML.documentElement.getAttribute("answer");
			alert("This is your email id " + answerFromXML);
		} catch (error) {

		   jslog("This is error" + error);
		}

	}
   //Type appropriate comment here, and begin script below
   if(g_form.isNewRecord()) {

	   g_form.setValue('u_description', g_user.userName);
   }
	jslog("Running Asynchronous + The user is " + g_user.getFullName() );

}
